import  {React , useState , useEffect, useReducer }   from 'react' ;
import axios from 'axios'; 
import "./App.css" ;

const initialState ={
  loading:true,
  error:"",
  post:{},
}

const reducer = (state , action) =>{
  switch(action.type){
    case "Succ":
      return {
        loading:false,
        error : "",
        post : action.payload
      }
      case "Error":
      return {
        loading:false,
        error : "you have problem",
        post : {},
      }
  }
}

function App() {
  const  [state , dispatch] = useReducer(reducer , initialState)

  useEffect(()=> {
    axios.get("https://jsonplaceholder.typicode.com/posts/10")
    .then((response) =>{
      dispatch ({type : "Succ" , payload : response.data})
    })
    .catch((error)=>{
      dispatch({type : "Error"})
    })
  },)
  return (
          <div>
           {state.loading ? "loading ..." : state.post.title}
           {state.error ? state.error : null}
          </div>
  )
}


export default App;
